$(document).ready(function() {

    $('#createCourseForm')
        .bootstrapValidator({
            feedbackIcons: {
                valid: 'glyphicon glyphicon-ok',
                invalid: 'glyphicon glyphicon-remove',
                validating: 'glyphicon glyphicon-refresh'
            },
            fields: {
                'name': {
                    validators: {
                        notEmpty: {
                            message: 'The Course Name required and cannot be empty'
                        }
                    }
                },
                'date': {
                    validators: {
                        notEmpty: {
                            message: 'The Starting Date required and cannot be empty'
                        }
                    }
                },
                'location': {
                    validators: {
                        notEmpty: {
                            message: 'The Course Location required and cannot be empty'
                        }
                    }
                },
                'seats': {
                	validators: {
                		notEmpty: {
                			message: 'The Seats required and cannot be empty'
                		}
                	}
                }
            }
        })
});

